function ProductQAWidget({ config = {}, productId }) {
  const [questions, setQuestions] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [showForm, setShowForm] = React.useState(false);
  const [newQuestion, setNewQuestion] = React.useState('');
  const [askerName, setAskerName] = React.useState('');
  const [askerEmail, setAskerEmail] = React.useState('');
  const [submitting, setSubmitting] = React.useState(false);
  const [submitted, setSubmitted] = React.useState(false);
  const [expandedQuestions, setExpandedQuestions] = React.useState({});

  const {
    title = 'Customer Questions & Answers',
    allowAnonymous = false,
    requireEmail = true,
    showCount = 5
  } = config;

  // Get productId from URL if not provided
  const getProductId = () => {
    if (productId) return productId;
    const path = window.location.pathname;
    const match = path.match(/\/products\/([^/]+)/);
    return match ? match[1] : null;
  };

  React.useEffect(() => {
    const pid = getProductId();
    if (!pid) {
      setLoading(false);
      return;
    }

    fetch(`/api/plugins/product-qa/exec/questions?productId=${pid}`)
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setQuestions(data.questions || []);
        }
      })
      .catch(err => console.error('Failed to load Q&A:', err))
      .finally(() => setLoading(false));
  }, [productId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newQuestion.trim()) return;

    setSubmitting(true);
    try {
      const pid = getProductId();
      const response = await fetch('/api/plugins/product-qa/exec/questions', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          productId: pid,
          question: newQuestion,
          askerName: askerName || 'Anonymous',
          askerEmail: askerEmail
        })
      });

      const data = await response.json();
      if (data.success) {
        setSubmitted(true);
        setNewQuestion('');
        setShowForm(false);
        setTimeout(() => setSubmitted(false), 5000);
      }
    } catch (error) {
      console.error('Failed to submit question:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const toggleExpand = (id) => {
    setExpandedQuestions(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  const formatDate = (dateStr) => {
    return new Date(dateStr).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (loading) {
    return React.createElement('div', {
      style: { padding: '20px', textAlign: 'center', color: '#6b7280' }
    }, 'Loading questions...');
  }

  return React.createElement('div', {
    style: {
      padding: '24px',
      background: '#f9fafb',
      borderRadius: '12px',
      marginTop: '24px'
    }
  },
    // Header
    React.createElement('div', {
      style: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '20px'
      }
    },
      React.createElement('h3', {
        style: {
          fontSize: '20px',
          fontWeight: '600',
          color: '#111827',
          margin: 0
        }
      }, title),
      React.createElement('button', {
        onClick: () => setShowForm(!showForm),
        style: {
          padding: '10px 20px',
          background: '#3b82f6',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          fontWeight: '500',
          cursor: 'pointer'
        }
      }, showForm ? 'Cancel' : 'Ask a Question')
    ),

    // Success message
    submitted && React.createElement('div', {
      style: {
        background: '#d1fae5',
        color: '#065f46',
        padding: '12px 16px',
        borderRadius: '8px',
        marginBottom: '16px'
      }
    }, 'Your question has been submitted and will appear once approved.'),

    // Question form
    showForm && React.createElement('form', {
      onSubmit: handleSubmit,
      style: {
        background: 'white',
        padding: '20px',
        borderRadius: '8px',
        marginBottom: '20px',
        border: '1px solid #e5e7eb'
      }
    },
      React.createElement('textarea', {
        value: newQuestion,
        onChange: (e) => setNewQuestion(e.target.value),
        placeholder: 'What would you like to know about this product?',
        required: true,
        style: {
          width: '100%',
          minHeight: '100px',
          padding: '12px',
          border: '1px solid #d1d5db',
          borderRadius: '8px',
          fontSize: '14px',
          marginBottom: '12px',
          resize: 'vertical'
        }
      }),

      React.createElement('div', {
        style: {
          display: 'grid',
          gridTemplateColumns: '1fr 1fr',
          gap: '12px',
          marginBottom: '12px'
        }
      },
        !allowAnonymous && React.createElement('input', {
          type: 'text',
          value: askerName,
          onChange: (e) => setAskerName(e.target.value),
          placeholder: 'Your name',
          required: !allowAnonymous,
          style: {
            padding: '10px 12px',
            border: '1px solid #d1d5db',
            borderRadius: '8px',
            fontSize: '14px'
          }
        }),
        requireEmail && React.createElement('input', {
          type: 'email',
          value: askerEmail,
          onChange: (e) => setAskerEmail(e.target.value),
          placeholder: 'Your email (for notification)',
          required: requireEmail,
          style: {
            padding: '10px 12px',
            border: '1px solid #d1d5db',
            borderRadius: '8px',
            fontSize: '14px'
          }
        })
      ),

      React.createElement('button', {
        type: 'submit',
        disabled: submitting,
        style: {
          padding: '12px 24px',
          background: submitting ? '#9ca3af' : '#10b981',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          fontWeight: '500',
          cursor: submitting ? 'not-allowed' : 'pointer'
        }
      }, submitting ? 'Submitting...' : 'Submit Question')
    ),

    // Questions list
    questions.length === 0
      ? React.createElement('p', {
          style: {
            textAlign: 'center',
            color: '#6b7280',
            padding: '40px 20px'
          }
        }, 'No questions yet. Be the first to ask!')
      : React.createElement('div', {
          style: { display: 'flex', flexDirection: 'column', gap: '16px' }
        },
          questions.slice(0, showCount).map(q =>
            React.createElement('div', {
              key: q.id,
              style: {
                background: 'white',
                borderRadius: '8px',
                padding: '16px',
                border: '1px solid #e5e7eb'
              }
            },
              // Question
              React.createElement('div', {
                style: {
                  display: 'flex',
                  gap: '12px',
                  marginBottom: q.answer ? '12px' : 0
                }
              },
                React.createElement('span', {
                  style: {
                    fontWeight: '700',
                    color: '#3b82f6',
                    fontSize: '16px'
                  }
                }, 'Q:'),
                React.createElement('div', { style: { flex: 1 } },
                  React.createElement('p', {
                    style: {
                      fontWeight: '500',
                      color: '#111827',
                      marginBottom: '4px'
                    }
                  }, q.question),
                  React.createElement('p', {
                    style: {
                      fontSize: '12px',
                      color: '#9ca3af'
                    }
                  }, `Asked by ${q.asker_name} on ${formatDate(q.created_at)}`)
                )
              ),

              // Answer
              q.answer && React.createElement('div', {
                style: {
                  display: 'flex',
                  gap: '12px',
                  paddingTop: '12px',
                  borderTop: '1px solid #e5e7eb'
                }
              },
                React.createElement('span', {
                  style: {
                    fontWeight: '700',
                    color: '#10b981',
                    fontSize: '16px'
                  }
                }, 'A:'),
                React.createElement('div', { style: { flex: 1 } },
                  React.createElement('p', {
                    style: { color: '#374151' }
                  }, q.answer),
                  React.createElement('p', {
                    style: {
                      fontSize: '12px',
                      color: '#9ca3af',
                      marginTop: '4px'
                    }
                  }, `Answered on ${formatDate(q.answered_at)}`)
                )
              )
            )
          )
        ),

    // Show more
    questions.length > showCount && React.createElement('button', {
      onClick: () => setExpandedQuestions({})},
      style: {
        marginTop: '16px',
        width: '100%',
        padding: '12px',
        background: 'transparent',
        border: '1px solid #d1d5db',
        borderRadius: '8px',
        color: '#6b7280',
        cursor: 'pointer'
      }
    }, `Show all ${questions.length} questions`)
  );
}

