async function getQuestions(req, res, { sequelize }) {
  const { productId, status = 'approved', limit = 20 } = req.query;

  if (!productId) {
    return res.status(400).json({ success: false, error: 'Product ID required' });
  }

  try {
    const questions = await sequelize.query(`
      SELECT * FROM product_questions
      WHERE product_id = $1 AND status = $2
      ORDER BY created_at DESC
      LIMIT $3
    `, {
      bind: [productId, status, parseInt(limit)],
      type: sequelize.QueryTypes.SELECT
    });

    return res.json({ success: true, questions });
  } catch (error) {
    return res.status(500).json({ success: false, error: error.message });
  }
}

async function submitQuestion(req, res, { sequelize }) {
  const { productId, question, askerName, askerEmail } = req.body;

  if (!productId || !question) {
    return res.status(400).json({ success: false, error: 'Product ID and question required' });
  }

  try {
    const result = await sequelize.query(`
      INSERT INTO product_questions (product_id, question, asker_name, asker_email, status)
      VALUES ($1, $2, $3, $4, 'pending')
      RETURNING *
    `, {
      bind: [productId, question, askerName || 'Anonymous', askerEmail],
      type: sequelize.QueryTypes.INSERT
    });

    return res.json({ success: true, question: result[0] });
  } catch (error) {
    return res.status(500).json({ success: false, error: error.message });
  }
}

async function getPendingQuestions(req, res, { sequelize }) {
  const { limit = 50 } = req.query;

  try {
    const questions = await sequelize.query(`
      SELECT pq.*, p.name as product_name
      FROM product_questions pq
      LEFT JOIN products p ON pq.product_id::uuid = p.id
      WHERE pq.status = 'pending'
      ORDER BY pq.created_at ASC
      LIMIT $1
    `, {
      bind: [parseInt(limit)],
      type: sequelize.QueryTypes.SELECT
    });

    return res.json({ success: true, questions });
  } catch (error) {
    return res.status(500).json({ success: false, error: error.message });
  }
}

async function answerQuestion(req, res, { sequelize }) {
  const { questionId, answer, answeredBy } = req.body;

  if (!questionId || !answer) {
    return res.status(400).json({ success: false, error: 'Question ID and answer required' });
  }

  try {
    await sequelize.query(`
      UPDATE product_questions
      SET answer = $1, answered_at = NOW(), answered_by = $2, status = 'approved'
      WHERE id = $3
    `, {
      bind: [answer, answeredBy || 'Store', questionId],
      type: sequelize.QueryTypes.UPDATE
    });

    return res.json({ success: true, message: 'Question answered' });
  } catch (error) {
    return res.status(500).json({ success: false, error: error.message });
  }
}

async function updateQuestionStatus(req, res, { sequelize }) {
  const { questionId, status } = req.body;

  if (!questionId || !status) {
    return res.status(400).json({ success: false, error: 'Question ID and status required' });
  }

  const validStatuses = ['pending', 'approved', 'rejected'];
  if (!validStatuses.includes(status)) {
    return res.status(400).json({ success: false, error: 'Invalid status' });
  }

  try {
    await sequelize.query(`
      UPDATE product_questions
      SET status = $1
      WHERE id = $2
    `, {
      bind: [status, questionId],
      type: sequelize.QueryTypes.UPDATE
    });

    return res.json({ success: true, message: 'Status updated' });
  } catch (error) {
    return res.status(500).json({ success: false, error: error.message });
  }
}

async function markHelpful(req, res, { sequelize }) {
  const { questionId } = req.body;

  if (!questionId) {
    return res.status(400).json({ success: false, error: 'Question ID required' });
  }

  try {
    await sequelize.query(`
      UPDATE product_questions
      SET helpful_count = helpful_count + 1
      WHERE id = $1
    `, {
      bind: [questionId],
      type: sequelize.QueryTypes.UPDATE
    });

    return res.json({ success: true, message: 'Marked as helpful' });
  } catch (error) {
    return res.status(500).json({ success: false, error: error.message });
  }
}

