function QAManagementPage({ plugin }) {
  const [questions, setQuestions] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [filter, setFilter] = React.useState('pending');
  const [answerText, setAnswerText] = React.useState({});

  const loadQuestions = async () => {
    setLoading(true);
    try {
      const endpoint = filter === 'pending' ? '/pending' : `/questions?status=${filter}`;
      const response = await fetch(`/api/plugins/product-qa/exec${endpoint}&limit=100`);
      const data = await response.json();
      if (data.success) {
        setQuestions(data.questions || []);
      }
    } catch (error) {
      console.error('Failed to load questions:', error);
    } finally {
      setLoading(false);
    }
  };

  React.useEffect(() => {
    loadQuestions();
  }, [filter]);

  const handleAnswer = async (questionId) => {
    const answer = answerText[questionId];
    if (!answer) return;

    try {
      const response = await fetch('/api/plugins/product-qa/exec/answer', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ questionId, answer })
      });
      const data = await response.json();
      if (data.success) {
        loadQuestions();
        setAnswerText(prev => ({ ...prev, [questionId]: '' }));
      }
    } catch (error) {
      console.error('Failed to answer:', error);
    }
  };

  const handleStatus = async (questionId, status) => {
    try {
      await fetch('/api/plugins/product-qa/exec/status', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ questionId, status })
      });
      loadQuestions();
    } catch (error) {
      console.error('Failed to update status:', error);
    }
  };

  return React.createElement('div', { style: { padding: '24px' } },
    React.createElement('h1', { style: { fontSize: '24px', marginBottom: '20px' } }, 'Q&A Management'),

    // Filter tabs
    React.createElement('div', { style: { display: 'flex', gap: '8px', marginBottom: '20px' } },
      ['pending', 'approved', 'rejected'].map(status =>
        React.createElement('button', {
          key: status,
          onClick: () => setFilter(status),
          style: {
            padding: '8px 16px',
            background: filter === status ? '#3b82f6' : '#e5e7eb',
            color: filter === status ? 'white' : '#374151',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            textTransform: 'capitalize'
          }
        }, status)
      )
    ),

    loading
      ? React.createElement('p', null, 'Loading...')
      : questions.length === 0
        ? React.createElement('p', { style: { color: '#6b7280' } }, `No ${filter} questions`)
        : React.createElement('div', { style: { display: 'flex', flexDirection: 'column', gap: '16px' } },
            questions.map(q =>
              React.createElement('div', {
                key: q.id,
                style: { background: 'white', padding: '16px', borderRadius: '8px', border: '1px solid #e5e7eb' }
              },
                React.createElement('p', { style: { fontWeight: '600', marginBottom: '8px' } }, q.question),
                React.createElement('p', { style: { fontSize: '12px', color: '#6b7280', marginBottom: '12px' } },
                  `${q.asker_name} - ${q.product_name || q.product_id}`
                ),

                filter === 'pending' && React.createElement('div', null,
                  React.createElement('textarea', {
                    value: answerText[q.id] || '',
                    onChange: (e) => setAnswerText(prev => ({ ...prev, [q.id]: e.target.value })),
                    placeholder: 'Type your answer...',
                    style: { width: '100%', padding: '8px', marginBottom: '8px', borderRadius: '4px', border: '1px solid #d1d5db' }
                  }),
                  React.createElement('div', { style: { display: 'flex', gap: '8px' } },
                    React.createElement('button', {
                      onClick: () => handleAnswer(q.id),
                      style: { padding: '6px 12px', background: '#10b981', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }
                    }, 'Answer & Approve'),
                    React.createElement('button', {
                      onClick: () => handleStatus(q.id, 'rejected'),
                      style: { padding: '6px 12px', background: '#ef4444', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }
                    }, 'Reject')
                  )
                ),

                q.answer && React.createElement('div', {
                  style: { background: '#f0fdf4', padding: '12px', borderRadius: '6px', marginTop: '8px' }
                },
                  React.createElement('p', { style: { fontSize: '12px', color: '#166534', marginBottom: '4px' } }, 'Answer:'),
                  React.createElement('p', null, q.answer)
                )
              )
            )
          )
  );
}
